sys_script_include.script:
var MFReferenceHelper = Class.create();
MFReferenceHelper.prototype = {
    initialize: function() {
    },

	limitViewsToTable: function () {

		var gr = new GlideRecord('sys_ui_section');
		gr.addQuery('name', 'problem');
		gr.query();

		var viewNames = [];

		while (gr.next()) {
			viewNames.push(gr.view.getDisplayValue());
		}

		var q = 'nameIN' + viewNames.join(',');

		q = q + '^ORnameISEMPTY';

		return q;
	},

	relatedFormSections: function () {

		var q = '';

		/*if (parent.table) {
			q = 'name=' + parent.table;
		}

		if (parent.view) {
			if (q !== '') {
				q = q + '^';
			}

			q = q + 'view=' + parent.view;
		}*/

		return q;
	},

    type: 'MFReferenceHelper'
};
